def time_theta(test_index, test_N=1):
    """
    Selects a kernel generating an isogeny between elliptic products from
    `isogeny_diamond.py` using `generate_splitting_kernel()` and times
    the average computation time of codomain and evaluation time for the
    isogeny chain in the theta model.
    """
    (P1, Q1, P2, Q2), _ = generate_splitting_kernel(test_index)

    # Create kernel from CouplePoint data
    ker_Phi = (CouplePoint(P1, P2), CouplePoint(Q1, Q2))
    EA, EB = ker_Phi[0].curves()

    _, ea, eb, _, _ = DIAMONDS[test_index]
    PB3, _ = torsion_basis(EB, 3**eb)

    strategy = optimised_strategy(ea)

    # start profiler
    t0 = time.process_time_ns()
    for _ in range(test_N):
        # Compute the (2^ea,2^ea)-isogeny
        Phi = EllipticProductIsogeny(ker_Phi, ea, strategy=strategy)
    print(
        f"Theta Model codomain took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )

    t0 = time.process_time_ns()
    # Compute the time to push a point through the isogeny
    L1 = CouplePoint(EA(0), PB3)
    for _ in range(test_N):
        _ = Phi(L1)
    print(
        f"Theta Model image took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )


def time_theta_sqrt(test_index, test_N=1):
    """
    Selects a kernel generating an isogeny between elliptic products from
    `isogeny_diamond.py` using `generate_splitting_kernel()` and times
    the average computation time of codomain and evaluation time for the
    isogeny chain in the theta model without additional torsion available.
    """
    (P1, Q1, P2, Q2), _ = generate_splitting_kernel(test_index)

    # Create kernel from CouplePoint data
    ker_Phi = (CouplePoint(4 * P1, 4 * P2), CouplePoint(4 * Q1, 4 * Q2))
    EA, EB = ker_Phi[0].curves()

    _, ea, eb, _, _ = DIAMONDS[test_index]
    PB3, _ = torsion_basis(EB, 3**eb)

    strategy = optimised_strategy(ea - 2)

    # start profiler
    t0 = time.process_time_ns()
    for _ in range(test_N):
        # Compute the (2^ea,2^ea)-isogeny
        Phi = EllipticProductIsogenySqrt(ker_Phi, ea, strategy=strategy)
    print(
        f"Theta Model Sqrt codomain took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )

    t0 = time.process_time_ns()
    # Compute the time to push a point through the isogeny
    L1 = CouplePoint(EA(0), PB3)
    for _ in range(test_N):
        _ = Phi(L1)
    print(
        f"Theta Model Sqrt image took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )


def time_mumford(test_index, test_N=1):
    """
    Selects a kernel generating an isogeny between elliptic products from
    `isogeny_diamond.py` using `generate_splitting_kernel()` and times
    the average computation time of codomain and evaluation time for the
    isogeny chain in the  Mumford model.
    """
    ker_Phi, (E0, _) = generate_splitting_kernel(test_index)
    EA = ker_Phi[0].curve()
    EB = ker_Phi[2].curve()

    _, ea, eb, _, _ = DIAMONDS[test_index]
    A = ZZ(2**ea)
    B = ZZ(3**eb)
    C = A - B
    PB3, _ = torsion_basis(EB, 3**eb)

    F = EA.base_ring()
    N1 = B
    N2 = C
    N_constant = F(N1 + N2) / F(N1 - N2)
    strategy = optimised_strategy_old(ea - 1, mul_c=0.175)

    # Compute the chain and make sure it splits
    (P1, Q1, P2, Q2) = ker_Phi
    ker_Phi_scaled = (4 * P1, 4 * Q1, 4 * P2, 4 * Q2)

    t0 = time.process_time_ns()
    for _ in range(test_N):
        Phi, (_, _) = compute_richelot_chain(ker_Phi_scaled, ea, N_constant, strategy)
    print(
        f"Mumford Model codomain took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )

    # One of these points will have full order, which we can
    # recover the secret from
    t0 = time.process_time_ns()
    for _ in range(test_N):
        _ = evaluate_richelot_chain(Phi, (None, PB3))
    print(
        f"Mumford Model image took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )


def time_dim_one(test_index, test_N=1):
    """
    Computes an isogeny between elliptic curves of degree 2^ea
    using x-only arithmetic
    """
    _, ea, eb, _, _ = DIAMONDS[test_index]
    (P1, Q1, _, _), _ = generate_splitting_kernel(test_index)

    EA = P1.curve()
    P3, _ = torsion_basis(EA, 3**eb)
    P1, Q1 = fix_torsion_basis_renes(P1, Q1, ea + 2)
    A = ZZ(2**ea)

    KA = KummerLine(EA)
    ker_A = KA(4 * (P1 + 123 * Q1))
    P = KA(P3)

    # start profiler
    t0 = time.process_time_ns()
    for _ in range(test_N):
        # Compute the (2^ea,2^ea)-isogeny
        Phi = KummerLineIsogeny(KA, ker_A, A, check=False)
    print(
        f"Dim One Montgomery Model codomain took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )

    t0 = time.process_time_ns()
    # Compute the time to push a point through the isogeny
    for _ in range(test_N):
        _ = Phi(P)
    print(
        f"Dim One Montgomery Model image took: {(time.process_time_ns() - t0) / (1_000_000 * test_N):.5f} ms"
    )


